promqlQuery.value:
label_replace(
  count by (k8s_cluster_name,k8s_deployment_name,k8s_daemonset_name) (
    k8s_pod_phase_1{
      k8s_cluster_name=~"{{k8s_cluster_name}}",
      k8s_node_name=~"{{k8s_node_name}}"
    }[${__range}] == 0
  ),
  "state", "Pending", "", ""
)
or
label_replace(
  count by (k8s_cluster_name,k8s_deployment_name,k8s_daemonset_name) (
    k8s_pod_phase_1{
      k8s_cluster_name=~"{{k8s_cluster_name}}",
      k8s_node_name=~"{{k8s_node_name}}"
    }[${__range}] == 1
  ),
  "state", "Running", "", ""
)
